import React from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import Card from 'react-bootstrap/Card';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch } from '@fortawesome/free-solid-svg-icons'
import { useSelector, useDispatch } from 'react-redux';
import { search } from '../../redux/reducers/bestpriceSlice';
import { useHistory } from "react-router-dom";
// search para poder usar el "dispatch" y enviar el evento  al store redux
// useSelector para leer datos del store, useDispatch para enviar actions.

function Navigator() {
    const dispatch = useDispatch();
    const history = useHistory();
        
    return (
        <Navbar bg="dark" variant="dark">
            <Container>
                <Navbar.Brand href="#home"> Coins </Navbar.Brand>
                <Button variant="outline-info" style={{marginRight: 2}} onClick={() => history.push("/crearPartida")}>
                    searchCoins
                    </Button>{' '}
                <Nav className="me-auto">
                    <Button variant="outline-info" style={{marginRight: 2}} onClick={() => history.push("/")}>
                        Home
                    </Button>            
                    <Button variant="outline-info" type='button' onClick={() => {
                        let xhr = new XMLHttpRequest();
                        let url = 'http://127.0.0.1:8000/api/game/list'
                        xhr.open('GET', url);
                        xhr.send();
                        xhr.onload = function () {
                                let responseObj = JSON.parse(xhr.response);
                                dispatch(search(responseObj))
                            };
                        }}>
                        All Coins
                    </Button>
                </Nav>
                <Form className="d-flex">
                    <Form.Control
                        type="text"
                        placeholder="Search Coin"
                        className="mr-2"
                        aria-label="Search"
                        id="filterControl"
                    />
                    <Button variant="outline-success" type='button' value='Search'
                        onClick={() => {
                            let xhr = new XMLHttpRequest();
                            let filter = document.getElementById("filterControl").value
                            if(filter.replace(/\s+/g, '') === ""){
                                alert('Este campo no puede estar vacío');
                            }else{
                                let url = 'http://127.0.0.1:8000/api/game/list?filtro='.concat(String(filter));
                                xhr.open('GET', url);
                                xhr.send();
                                xhr.onload = function () {
                                    let responseObj = JSON.parse(xhr.response);
                                    dispatch(search(responseObj))
                                };
                            }
                        }
                        }
                    >
                        <FontAwesomeIcon icon={faSearch}> </FontAwesomeIcon>
                    </Button>
                </Form>
            </Container>
        </Navbar>
    );
}


function Coins(props) {
    return (
        <Col className='mb-3' md={4} >
            <Card style={{ width: '13rem' }}>
                <Card.Body>
                    <Card.Title> {props.name} (#{props.ID}) </Card.Title>
                    <Card.Text>
                        Texto de ejemplo por si acaso.
                    </Card.Text>
                    <Row>
                        <Col md={{ offset: 5 }}>
                        </Col>
                    </Row>
                </Card.Body>
            </Card>
        </Col>
    );
}

function BestPrice() {
    const bestprice = useSelector((state) => state.bestprice.value);

    return (
        <div className="Partida">
            <div className="App-navbar">
                <Navigator> </Navigator>
            </div>
            <Container className='pt-4'>
                <Row>
                    {bestprice.map(Coins)}
                </Row>
            </Container>
        </div>
    );
}

export default BestPrice;
