import 'bootstrap/dist/css/bootstrap.min.css';
import Container from 'react-bootstrap/Container';
import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import { useHistory } from "react-router-dom";
import './Lobby.css';
import { useSelector, useDispatch } from 'react-redux';
import { resetUser } from '../../redux/reducers/usuariosSlice';
import { resetState } from '../../redux/reducers/turnoSlice';
import { reset_partidas } from '../../redux/reducers/partidasSlice';

function Coin(props) {
    return (
        <Card style={{ width: '13rem' }} text={"white bg-success mb-3"}>
            <h5 class="card-header">
                {props.nickname}
            </h5>
            <h6 class="card-title mb-2">
                {props.others}
            </h6>
        </Card>
    );
}

function OrdenLobby(props) {
    return (
        <Container className='pt-4'>
            <Row>
                <Col xs={6} md={5}>
                    {props.col1}
                </Col>
                <Col xs={6} md={5}>
                    {props.col2}
                </Col>
                <Col xs={6} md={2}>
                    {props.col3}
                </Col>
            </Row>
        </Container>
    );
}

function Lobby(props) {
    const history = useHistory();
    const partida = useSelector((state) => state.turno.game);
    const usuarios = useSelector((state) => state.usuarios.users);
    const mi_user = useSelector((state) => state.turno.user)
    const dispatch = useDispatch();
    return (
        <div class="container" >
            <Container className='pt-4'>
                <h1 className="WeTitle" >
                    {partida.game_name}
                </h1>
                <div style={{ marginRight: "10%" }} >
                    <OrdenLobby
                        col1="" col2={<Coin nickname={usuarios[0].nickname} others="P1estado"></Coin>} col3="">
                    </OrdenLobby>
                    <OrdenLobby
                        col1={<Coin nickname={usuarios[1].nickname} others="P2estado"></Coin>}
                        col2=""
                        col3={<Coin nickname={usuarios[2].nickname} others="P3estado"></Coin>}>
                    </OrdenLobby>
                    <OrdenLobby
                        col1=""
                        col2={
                            <button className="button-54" onClick={() => {
                                var flag = 0;
                                let game_id = partida.game_id;
                                let url = 'http://127.0.0.1:8000/api/game/init/'.concat(game_id);
                                fetch(url, {
                                    method: 'PUT',
                                    headers: {
                                        'Content-Type': 'application/json',
                                    },
                                    body: JSON.stringify({ name: partida.game_name, idOwner: mi_user.user_id })
                                }).then(response => {
                                    response.json();
                                    if (response.status === 200)
                                        flag = 1;
                                }).then(data => {
                                    if (!flag) {
                                        alert("No eres el host y/o la partida no contiene suficiente jugadores");
                                    }
                                    else {
                                        history.push("/turno");
                                    }

                                })
                            }}>
                                Iniciar partida
                            </button>
                        }
                        col3="">
                    </OrdenLobby>
                    <OrdenLobby
                        col1={<Coin nickname={usuarios[3].nickname} others="P4estado"></Coin>} col2=""
                        col3={<Coin nickname={usuarios[4].nickname} others="P5estado"></Coin>}>
                    </OrdenLobby>
                    <OrdenLobby
                        col1={
                            <Button variant="secondary" onClick={() => {
                                fetch('http://127.0.0.1:8000/api/game/quit', {
                                    method: 'POST',
                                    headers: {
                                        'Content-Type': 'application/json',
                                    },
                                    body: JSON.stringify({ idGame: partida.game_id, idCoin: mi_user.user_id })
                                })
                                    .then(response => response.json())
                                    .then(data => {
                                        dispatch(resetUser());
                                        dispatch(resetState());
                                        dispatch(reset_partidas());
                                        history.push("/partida")
                                    })

                            }}>
                                Abandonar partida
                            </Button>
                        }
                        col2={<Coin nickname={usuarios[5].nickname} others="P6estado"></Coin>}
                        col3="">
                    </OrdenLobby>
                </div>
            </Container>
        </div>
    );
}

function Trade() {
  return (
    <div className="bg">
      <Container>
        <Row>
          <text>
            Nada Trade
            </text>
        </Row>
      </Container>
    </div>
  )
};

export default Trade;
