import 'bootstrap/dist/css/bootstrap.min.css';
import Container from 'react-bootstrap/Container';
import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { useHistory } from "react-router-dom";
import { useSelector, useDispatch } from 'react-redux';
import './Trade.css';

function Coin(props) {
    return (
        <Card style={{ width: '13rem' }} text={"white bg-success mb-3"}>
            <h5 class="card-header">
                {props.nickname}
            </h5>
            <h6 class="card-title mb-2">
                {props.others}
            </h6>
        </Card>
    );
}

function OrdenLobby(props) {
    return (
        <Container className='pt-4'>
            <Row>
                <Col xs={6} md={5}>
                    {props.col1}
                </Col>
                <Col xs={6} md={5}>
                    {props.col2}
                </Col>
                <Col xs={6} md={2}>
                    {props.col3}
                </Col>
            </Row>
        </Container>
    );
}

function Trade(props) {
    
 const handleSubmit = (event) => {
  const form = event.currentTarget;
  if (form.checkValidity() === false) {
    event.preventDefault();
    event.stopPropagation();
  } else {
    let nombre_usuario = document.getElementById('nickname').value
    let nombre_partida = document.getElementById('nombrePartida').value
    var id_usuario = -1;
    var id_partida = -1;
    var ws = "";

    fetch('http://127.0.0.1:8000/api/player/create/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ nickname: nombre_usuario }),
    })
      .then(response => response.json())
      .then(data => {
        id_usuario = data.player.id;
        fetch('http://127.0.0.1:8000/api/game/create', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ name: nombre_partida, idOwner: id_usuario })
        })
          .then(response => response.json())
          .then(data => {
            id_partida = data.idPartida;
            let pathGame = "ws://localhost:8000/ws/turno/".concat(String(id_partida)).concat("/", id_usuario);
            ws = new WebSocket(pathGame);
            ws.onmessage = function (event) {
              var mess = JSON.parse(event.data)
              switch (mess.type) {
                case "join":
                  dispatch(joinUser({ user_id: mess.user.user_id, nickname: mess.user.nickname }));
                  break;
                case "quit":
                  dispatch(quitUser(mess.user))
                  break;
                case "close":
                  ws.close();
                  break;
                case "dice":
                  dispatch(setDice(mess.user))
                  break;
                case "cards":
                  dispatch(setCards(mess.deck))
                  break;
                case "players_turn":
                  console.log(mess)
                  dispatch(setTurn(mess.players))
                  break;
                case "pass_turn":
                  const firstElement = mess.list[0]
                  console.log(mess.list)
                  console.log(mess)
                  var array = mess.list
                  var array2 = [...array]
                  array2.shift()
                  array2.push(firstElement)
                  console.log(array2 + " Array con first")
                  dispatch(setTurn(array2))
                  break;
                default:
              }
            }
          })
          .catch((error) => {
            console.error('Error:', error);
          });
      })
      .catch((error) => {
        console.error('Error:', error);
      });

    history.push("/lobby");
   }
   setValidated(true);
   };

    const history = useHistory();
    //const partida = useSelector((state) => state.turno.game);
    //const usuarios = useSelector((state) => state.usuarios.users);
    //const mi_user = useSelector((state) => state.turno.user)
    const dispatch = useDispatch();
    return (
        <div class="container" >
            <Container className='pt-4'>
                <h1 className="WeTitle" >
                    Buy && Sell
                </h1>
                <div style={{ marginRight: "10%" }} >
                   <Form noValidate validated={validate} onSubmit={handleSubmit}>
                      <Form.Group className="mb-3">
                        <Form.Label>Nickname</Form.Label>
                        <Form.Control
                          id="nickname"
                          required
                          type="text"
                          placeholder="Ingrese su nickname"
                          pattern="([a-zA-Z0-9]){4,20}"
                        />
                        <Form.Control.Feedback type="invalid">Debe contener entre 4 y 20 caracteres, solo letras y números sin espacios.</Form.Control.Feedback>
                      </Form.Group>
                      <Form.Group className="mb-3" controlId="validationCustom02">
                        <Form.Label>Nombre de partida</Form.Label>
                        <Form.Control
                          id="nombrePartida"
                          required
                          type="text"
                          placeholder="Ingrese el nombre de la partida"
                          pattern="([a-zA-Z0-9]){4,20}"
                        />
                        <Form.Control.Feedback type="invalid">Debe contener entre 4 y 20 caracteres, solo letras y números sin espacios.</Form.Control.Feedback>
                      </Form.Group>
                      <Button variant="primary" type="submit" disabled={handleSubmit}>
                        Crear
                      </Button>{' '}
                      <Button variant="secondary" onClick={() => history.push("/partida")}>Cerrar</Button>
                    </Form>
                    <OrdenLobby
                        col1=""
                        col2={
                            <button className="button-54" onClick={() => {
                                var flag = 0;
                                //let game_id = partida.game_id;
                                let url = 'http://127.0.0.1:8000/api/game/init/';
                                fetch(url, {
                                    method: 'PUT',
                                    headers: {
                                        'Content-Type': 'application/json',
                                    },
                                    body: JSON.stringify({ name: "coin.name", idOwner: "coin.id" })
                                }).then(response => {
                                    response.json();
                                    if (response.status === 200)
                                        flag = 1;
                                }).then(data => {
                                    if (!flag) {
                                        alert("No eres el host y/o la partida no contiene suficiente jugadores");
                                    }
                                    else {
                                        history.push("/turno");
                                    }

                                })
                            }}>
                                Ok
                            </button>
                        }
                        col3="">
                    </OrdenLobby>
                </div>
            </Container>
        </div>
    );
}

export default Trade;
