import 'bootstrap/dist/css/bootstrap.min.css';
import Container from 'react-bootstrap/Container';
import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { useHistory } from "react-router-dom";
import { useSelector, useDispatch } from 'react-redux';
import './Trade.css';
import React, { useState } from 'react';


function Coin(props) {
    return (
        <Card style={{ width: '13rem' }} text={"white bg-success mb-3"}>
            <h5 class="card-header">
                {props.nickname}
            </h5>
            <h6 class="card-title mb-2">
                {props.others}
            </h6>
        </Card>
    );
}

function OrdenLobby(props) {
    return (
        <Container className='pt-4'>
            <Row>
                <Col xs={6} md={5}>
                    {props.col1}
                </Col>
                <Col xs={6} md={5}>
                    {props.col2}
                </Col>
                <Col xs={6} md={2}>
                    {props.col3}
                </Col>
            </Row>
        </Container>
    );
}

function Trade(props) {
 const [validate, setValidated] = useState(false);
     
 const handleSubmit = (event) => {
  const form = event.currentTarget;
  if (form.checkValidity() === false) {
    event.preventDefault();
    event.stopPropagation();
  } else {
    let nombre_usuario = document.getElementById('nickname').value
    let nombre_partida = document.getElementById('nombrePartida').value
    var id_usuario = -1;
    var id_partida = -1;
    var ws = "";

    fetch('http://127.0.0.1:8000/api/player/create/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ nickname: nombre_usuario }),
    })
      .then(response => response.json())
      .then(data => {
        id_usuario = data.player.id;
        fetch('http://127.0.0.1:8000/api/game/create', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ name: nombre_partida, idOwner: id_usuario })
        })
          .then(response => response.json())
          .then(data => {
            id_partida = data.idPartida;
            let pathGame = "ws://localhost:8000/ws/turno/".concat(String(id_partida)).concat("/", id_usuario);
            ws = new WebSocket(pathGame);
            ws.onmessage = function (event) {
              var mess = JSON.parse(event.data)
              switch (mess.type) {
                case "join":
                  break;
                case "quit":
                  break;
                case "close":
                  ws.close();
                  break;
                case "dice":
                  break;
                case "cards":
                  break;
                case "players_turn":
                  console.log(mess)
                  break;
                case "pass_turn":
                  const firstElement = mess.list[0]
                  console.log(mess.list)
                  console.log(mess)
                  var array = mess.list
                  var array2 = [...array]
                  array2.shift()
                  array2.push(firstElement)
                  console.log(array2 + " Array con first")
                  break;
                default:
              }
            }
          })
          .catch((error) => {
            console.error('Error:', error);
          });
      })
      .catch((error) => {
        console.error('Error:', error);
      });

    history.push("/lobby");
   }
   setValidated(true);
   };

    const history = useHistory();
    //const partida = useSelector((state) => state.turno.game);
    //const usuarios = useSelector((state) => state.usuarios.users);
    //const mi_user = useSelector((state) => state.turno.user)
    const dispatch = useDispatch();
    return (
        <div class="container" >
            <Container className='pt-4'>
                <h1 className="WeTitle" >
                    Buy && Sell
                </h1>
                <Row>
                 <Col>  
                 <Form>
                    <fieldset enabled>
                      <Form.Group className="mb-3">
                        <Form.Label htmlFor="disabledTextInput">NameCoin</Form.Label>
                        <Form.Control
                          id="disabledTextInput"
                          required
                          type="text"
                          placeholder="Name Coin"
                          pattern="([A-Z]){0,3}-([A-Z]){0,5}"
                        />
                        <Form.Control.Feedback type="invalid">Coin must be type of ETH-USDT </Form.Control.Feedback>
                      </Form.Group>
                      <Form.Group className="mb-3">
                        <Form.Label htmlFor="disabledTextInput2">Price</Form.Label>
                        <Form.Control
                          id="disabledTextInput"
                          required
                          type="text"
                          placeholder="Name Coin"
                          pattern="([A-Z]){0,3}-([A-Z]){0,5}"
                        />
                        <Form.Control.Feedback type="invalid">Coin must be type of ETH-USDT </Form.Control.Feedback>
                     
                      </Form.Group>
                      <Form.Group className="mb-3">
                        <Form.Label htmlFor="disabledSelect">Money</Form.Label>
                        <Form.Select id="disabledSelect">
                          <option>USD</option>
                          <option>Peso Argentino</option>
                        </Form.Select>
                      </Form.Group>
                      <Form.Group className="mb-3">
                        <Form.Label htmlFor="disabledSelect">Select Buy or Sell</Form.Label>
                        <Form.Select id="disabledSelect">
                          <option>Buy</option>
                          <option>Sell</option>
                        </Form.Select>
                      </Form.Group>
                      <Form.Group className="mb-3">
                        <Form.Check
                          type="checkbox"
                          id="disabledFieldsetCheck"
                          label="Can't check this"
                        />
                      </Form.Group>
                      <Button type="submit">Submit</Button>
                      <Button type="submit" onClick={() => history.push("/")}>
                        Home
                      </Button>    
                    </fieldset>
                 </Form>
                 </Col>
                 <Col>
                 </Col>
                 </Row> 
            </Container>
        </div>
    );
}

export default Trade;
